import { useState } from "react";
import "./App.css";
function App() {
  const [toDos, setTodos] = useState([]);
  const [toDo, setTodo] = useState("");
  const [limitExpired, setLimitExpired] = useState(false);
  const deleteTodo = (id) => {
    setTodos(toDos.filter((todo) => todo.id !== id));
    setLimitExpired(false); // Reset the limit expired message when deleting todos
  };

  const handleAddTodo = () => {
    if (toDos.length < 3) {
      setTodos([...toDos, { id: Date.now(), text: toDo, status: false }]);
      setTodo("");
    } else {
      setLimitExpired(true);
    }
  };

  return (
    <div className="app">
      <div className="mainHeading">
        <h1>ToDo List</h1>
      </div>
      <div className="subHeading">
        <br />
        <h2>Not OneDay, it's Day Oneüåù ‚òïÔ∏è </h2>
      </div>
      <div className="input">
        <input
          value={toDo}
          onChange={(event) => setTodo(event.target.value)}
          type="text"
          placeholder="üñä Add item..."
        />
        <i
          className="fas fa-plus"
          onClick={() =>{
            setTodos([...toDos, { id: Date.now(), text: toDo, status: false }]);
            setTodo("");
          }   
          }
        ></i>
      </div>
      <div className="todos">
        {toDos.map((obj) => {
          return (
            <div className="todo">
              <div className="left">
                <input
                  onChange={(event) => {
                    console.log(event.target.checked);
                    console.log(obj);
                    setTodos(toDos.filter(obj2=>{
                      if (obj2.id===obj.id) {
                        obj2.status=event.target.checked
                      }
                      return obj2
                    }))
                  }}
                  value={obj.status}
                  type="checkbox"
                  name=""
                  id=""
                />
                <p>{obj.text}</p>
              </div>
              <div className="right">
                <i className="fas fa-times" onClick={() => deleteTodo(obj.id)}></i>
              </div>
            </div>
          );
        })}
        {
          toDos.map((obj)=>{
            if (obj.status) {
              return (<h1>{obj.text}</h1>)
            }
            return null
          })
        }
      </div>
    </div>
  );
}

export default App;
